import React from "react";
import styled from "styled-components";
import {colors, colorsRoles} from "../../01 Atoms/Colors";
import Button from "../../02 Molecules/Button";
import {media} from "../../01 Atoms/MediaQueries";
import {transitions} from "../../01 Atoms/Animations";


const NavCreativeDeveloperProjectItem = (props) => {

    const ToolItem = props.ToolItem;

    return <>
        <Box>
            <LeftSide FieldTextColorVisible={"black"} TextColor={"black"}>
                <LeftContent>
                    <p>{props.field}</p>
                    <h3>{props.title}</h3>
                    <p>{props.description}</p>
                    <Button pathname={props.slug} label={"See more"} LightMode/>
                </LeftContent>
            </LeftSide>
            <RightSide>

                    <ProjectPicture>
                        <Picture></Picture>
                        <ToolsBoxProject>
                            <p>built with</p>

                            {
                                props.ToolItem.map((item,index) => (
                                    <>
                                    <ToolItem
                                        key={index}
                                        src={item.logo}
                                        alt={item.altImage}
                                    />
                                    </>


                                ))
                            }

                        </ToolsBoxProject>
                    </ProjectPicture>

            </RightSide>
        </Box>
    </>

};

export default NavCreativeDeveloperProjectItem;

export const Box = styled.div`
position: relative;
width: 100%;
border: 1px solid red;
height: 640px;
display: flex;
