import React from "react";
import styled, {css} from "styled-components";



const PictureComponent = (props) => {
    return <>
        <AspectRatio
            video={props.video}
            portrait={props.portrait}
            landscape={props.landscape}
            carre={props.carre}
            rectangle={props.rectangle}
        >
            <img src={props.src}/>
        </AspectRatio>

    </>

};

export default PictureComponent;

export const AspectRatio = styled.div`
overflow: hidden;
position: relative;

${(props) => {
    return (
        props.videoportrait &&
        css`
            {
            padding-top: 56.25%;
            }
            `
    );
}}

${(props) => {
    return (
        props.videolandscape &&
        css`
            {
            padding-top: 56.25%;
            }
            `
    );
}}

${(props) => {
    return (
        props.carre &&
        css`
            {
            padding-top: 100%;
            }
            `
    );
}}

${(props) => {
    return (
        props.rectangle &&
        css`
            {
            padding-top: 75%;
            }
            `
    );
}}




& img {
position: absolute;
top: 0;
right: 0;
bottom: 0;
left: 0;

}

${(props) => {
    return (
        props.portrait &&
        css`
            & img {
            height: 100%;
            }
            `
    );
}}

    ${(props) => {
    return (
        props.landscape &&
        css`
            & img {
            width: 100%;
            }
            `
    );
}}


`;
