import React from "react";
import styled, {css} from "styled-components";



const PictureComponent = ({portrait, ...props}) => {
    return <>
        <AspectRatio ratio={props.ratio} style={{ maxWidth: '800px' }}>
            <img src={props.src} portrait={portrait} {...props} />
        </AspectRatio>

    </>

};

export default PictureComponent;

export const AspectRatio = styled.div`
overflow: hidden;
position: relative;

${(props) => {
    return (
        props.video &&
        css`
            {
            padding-top: 56.25%;
            }
            `
    );
}}

${(props) => {
    return (
        props.carre &&
        css`
            {
            padding-top: 100%;
            }
            `
    );
}}

${(props) => {
    return (
        props.rectangle &&
        css`
            {
            padding-top: 100%;
            }
            `
    );
}}


& img {
position: absolute;
top: 0;
right: 0;
bottom: 0;
left: 0;
height: 100%;

    ${(props) => {
        return (
            props.portrait &&
            css`
            {
            height: 100%;
            }
            `
        );
    }}

    ${(props) => {
        return (
            props.landscape &&
            css`
            {
            width: 100%;
            }
            `
        );
    }}

}


`;
