import React from "react";
import styled from "styled-components";
import {colors, colorsRoles} from "../../01 Atoms/Colors";
import Button from "../../02 Molecules/Button";
import {media} from "../../01 Atoms/MediaQueries";
import {transitions} from "../../01 Atoms/Animations";
import GitLogo from "../../../media/icons/Sass.svg";


const NavCreativeDeveloperProjectItem = () => {
    return <>
        <Box>
            <LeftSide FieldTextColorVisible={"black"} TextColor={"black"}>
                <LeftContent>
                    <p>Field</p>
                    <h3>Titre</h3>
                    <p>descripton</p>
                    <Button pathname={"/"} label={"See more"} LightMode/>
                </LeftContent>
            </LeftSide>
            <RightSide>

                    <ProjectPicture>
                        <Picture></Picture>
                        <ToolsBoxProject>
                            <p>made with</p>
                            <ToolItem src={GitLogo}></ToolItem>
                        </ToolsBoxProject>
                    </ProjectPicture>

            </RightSide>
        </Box>
    </>

};

export default NavCreativeDeveloperProjectItem;

export const Box = styled.div`
position: relative;
width: 100%;
border: 1px solid red;
height: 640px;
display: flex;
flex-direction: column-reverse;

${media.desktop`
height: 500px;
flex-direction: row;
`}
`;

export const LeftContent = styled.div`
height: 100%;
`;
export const LeftSide = styled.div`
border: 1px solid red;
