    line-height: 1.5;
    margin-bottom: 8px;
    text-align: left;
    ${media.desktop`
    margin-bottom: 16px;
    `}
    }

}
`;

export const RightSide = styled.div`
width: 100%;
display: flex;
justify-content: center;
align-items: center;

${media.desktop`
width: 50%;
height: 100%;
`}

`;


export const ToolsBoxProject = styled.div`
position: absolute;
width: 400px;
height: 96px;
background-color: ${colors.PureWhite};
//bottom: 0;
bottom: -160px;
right: 50%;
transform: translate(50%,-50%);
border-radius: 8px 8px 8px 0px;
z-index: 1;
transition: ${transitions.basic2};
`;

