        </Wrapper>

    </>

};

export default PictureComponent;

export const Wrapper = styled.div`
  width: 20%;
  overflow: hidden;
  height: 0;
  background-color: blue;

${(props) => {
    return (
        props.ratioSixteenNine &&
        css`
         padding-top: 56.25%;
          `
        );
}}
${(props) => {
    return (
        props.ratioFourThree &&
        css`
         padding-top: 56.25%;
          `
